nta.declaration:
const int 	N = 6;         // Number of tasks
typedef int[0,N-1] id_t;

broadcast chan		    release[N], ready[N], stop;
urgent broadcast chan 	run[N];

chan priority ready < default < release;
clock time;

// All times are in microseconds 

const int T[N] = {  // Periods
    10000, // BackgroundTask
    10000, // UpdateDisplayTask
    5000,  // MoveMotorsTask
    5000,  // ReciveDataTask
    1000,  // HandleLaserTask
    1000   // KeepUSBAliveTask
};	       

const int D[N] = {  // Deadlines
    10000, // BackgroundTask
    10000, // UpdateDisplayTask
    5000,  // MoveMotorsTask
    3000,  // ReciveDataTask
    1000,  // HandleLaserTask
    1000   // KeepUSBAliveTask
};	       

const int C[N] = {  // Computation Times
    5000,  // BackgroundTask
    480,  // UpdateDisplayTask
    28,   // MoveMotorsTask
    500,  // ReciveDataTask
    30,   // HandleLaserTask
    14    // KeepUSBAliveTask
};       

const int P[N] = {  // Priorities
    0,   // BackgroundTask
    1,   // UpdateDisplayTask
    2,   // MoveMotorsTask
    3,   // ReciveDataTask
    4,   // HandleLaserTask
    5    // KeepUSBAliveTask
};

const bool Pr[N] = {  // Preemptive
    true,   // BackgroundTask
    true,   // UpdateDisplayTask
    true,   // MoveMotorsTask
    false,  // ReciveDataTask
    false,  // HandleLaserTask
    false   // KeepUSBAliveTask
};

template.declaration:
id_t list[N] = { 5, 4, 3, 2, 1, 0};
int[0,N] len = N;

// Put an element at the end of the queue
void enqueue(id_t element)
{
int tmp=0;
list[len++] = element;
if (len>0)
{
	int i=len-1;
	while (i>0 && P[list[i]]>P[list[i-1]])
	{
		tmp = list[i-1];
		list[i-1] = list[i];
		list[i] = tmp;
		i--;
	}		
}
}

// Remove the front element of the queue
void dequeue()
{
        int i = 0;
        len -= 1;
        while (i < len)
        {
                list[i] = list[i + 1];
                i++;
        }
        list[i] = 0;
}

// Returns the front element of the queue
id_t front()
{
   return list[0];
}

// Returns the last element of the queue
id_t tail()
{
   return list[len - 1];
}

nta.system:
BackgroundTask    = Task( 0 );
UpdateDisplayTask = Task( 1 );
MoveMotorsTask    = Task( 2 );
ReciveDataTask    = Task( 3 );
HandleLaserTask   = Task( 4 );
KeepUSBAliveTask  = Task( 5 );


system 
       BackgroundTask, UpdateDisplayTask, HandleLaserTask, MoveMotorsTask, ReciveDataTask, KeepUSBAliveTask, Scheduler;

gantt {
    BackgroundTask    : BackgroundTask.Ready -> 5,    BackgroundTask.Running -> 1,    BackgroundTask.Blocked ->3, BackgroundTask.Error->6; 
    UpdateDisplayTask : UpdateDisplayTask.Ready -> 5, UpdateDisplayTask.Running -> 1, UpdateDisplayTask.Blocked ->3, UpdateDisplayTask.Error->6; 
    HandleLaserTask   : HandleLaserTask.Ready -> 5,   HandleLaserTask.Running -> 1,   HandleLaserTask.Blocked ->3, HandleLaserTask.Error->6; 
    MoveMotorsTask    : MoveMotorsTask.Ready -> 5,    MoveMotorsTask.Running -> 1,    MoveMotorsTask.Blocked ->3, MoveMotorsTask.Error->6; 
    ReciveDataTask    : ReciveDataTask.Ready -> 5,    ReciveDataTask.Running -> 1,    ReciveDataTask.Blocked ->3, ReciveDataTask.Error->6; 
    KeepUSBAliveTask  : KeepUSBAliveTask.Ready -> 5,  KeepUSBAliveTask.Running -> 1,  KeepUSBAliveTask.Blocked ->3, KeepUSBAliveTask.Error->6; 
}
